import React, {useState, useEffect} from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Admin_layout from '../../../pages/admin_layout';

const Products = ({ data, onRemove }) => {

	document.getElementById('test').innerHTML = <>
													<script src="%PUBLIC_URL%/plugins/jquery/jquery.min.js"></script>
												    <script src="%PUBLIC_URL%/plugins/bootstrap/js/bootstrap.bundle.min.js"></script>
												    <script src="%PUBLIC_URL%/plugins/overlayScrollbars/js/jquery.overlayScrollbars.min.js"></script>
												    <script src="%PUBLIC_URL%/dist/js/adminlte.js"></script>
												    <script src="%PUBLIC_URL%/dist/js/demo.js"></script>
												    <script src="%PUBLIC_URL%/plugins/jquery-mousewheel/jquery.mousewheel.js"></script>
												    <script src="%PUBLIC_URL%/plugins/raphael/raphael.min.js"></script>
												    <script src="%PUBLIC_URL%/plugins/jquery-mapael/jquery.mapael.min.js"></script>
												    <script src="%PUBLIC_URL%/plugins/jquery-mapael/maps/usa_states.min.js"></script>
												    <script src="%PUBLIC_URL%/plugins/chart.js/Chart.min.js"></script>
												    <script src="%PUBLIC_URL%/dist/js/pages/dashboard2.js"></script>
											    </>
	// useEffect(() => {
	//     const fetchData = async () => {
	//       const response = await axios.get(
	//         'http://localhost:3000/products',
	//       );
	//       setProducts(response.data);
	//     };
	//     fetchData();
	// }, []);

	const onHandleClick = (id) => {
		console.log(id);
		onRemove(id);
	}

	const section = <>
						  <table className="table">
						  <thead>
						    <tr>
						      <th scope="col">Id</th>
						      <th scope="col">Name</th>
						      <th scope="col">Image</th>
						      <th scope="col">Price</th>
						      <th scope="col">Details</th>
						      <th scope="col">Delete/Edit</th>
						    </tr>
						  </thead>
						  <tbody>
						    {data.map((product, index) => 
						    	<tr>
							      <th scope="row">{product.id}</th>
							      <td>{product.name}</td>
							      <td><img src={product.image} width='50' /></td>
							      <td>{product.price}</td>
							      <td>
									<Link to={`/admin/product/` + product.id}>
										<p>Detail</p>
									</Link>
							      </td>
							      <td>
							      	<button className="btn btn-danger mr-1" onClick={() => {const result = (window.confirm("delete")) ? onHandleClick(product.id) : false}}>Delete</button>
							      	<button className="btn btn-primary">Edit</button>
							      </td>
							    </tr>	
						    )}
						  </tbody>
						</table>
					</>
	return (
		<div>
			<Admin_layout section={section} />
		</div>
	)
}

export default Products
