import React, {useState} from 'react';
import { Link } from 'react-router-dom';
import Layout from '../layouts/layout.js';

const ShowProducts = ( {apiData, onRemove, onAdd} ) => {
	const name = null;
	const [product, setProduct] = useState({
	    "id": "21"
	  });
	const showHide = () => {
		let html = document.getElementById('addForm');
		if (html.style.display === "none") {
			html.style.display = "block";
		}else {
			html.style.display = "none";
		}
	}

	const onHandleClick = (id) => {
		onRemove(id);
	}

	const section = <>
						  <div>
						  	<Link to="/add-product">
						  		<button className="btn btn-primary mx-2" >Add</button>
						  	</Link>
						  </div>

						  <table className="table">
						  <thead>
						    <tr>
						      <th scope="col">Id</th>
						      <th scope="col">Name</th>
						      <th scope="col">Image</th>
						      <th scope="col">Price</th>
						      <th scope="col">Details</th>
						      <th scope="col">Delete/Edit</th>
						    </tr>
						  </thead>
						  <tbody>
						    {apiData.map((product, index) => 
						    	<tr>
							      <th scope="row">{product.id}</th>
							      <td>{product.name}</td>
							      <td><img src={product.image} width='50' /></td>
							      <td>{product.price}</td>
							      <td>
									<Link to={"/product/" + product.id}>
										<p>Detail</p>
									</Link>
							      </td>
							      <td>
							      	<button className="btn btn-danger mr-1" onClick={() => {const result = (window.confirm("delete")) ? onHandleClick(product.id) : false}}>Delete</button>
							      	<button className="btn btn-primary">Edit</button>
							      </td>
							    </tr>	
						    )}
						  </tbody>
						</table>
					</>
	return (
		<div>
			<Layout section={section} />
		</div>
	)
}

export default ShowProducts
