import React, {useState} from 'react';
import { Link, useHistory } from 'react-router-dom';
import Admin_layout from '../../../pages/admin_layout';

const Add_product = ({ data, onAdd }) => {
	const history = useHistory();
	const [ products, setProducts ] = useState(data);
	const [ product, setProduct ] = useState({});
	const id = parseInt(products[products.length-1].id) + 1;

	const onHandleSubmit = event => {
		event.preventDefault();
		onAdd(product);
		// history.replace("/admin/products");
	}

	const onHandleChange = event => {
		const {name, value} = event.target;
		setProduct(pre => {
			return {...pre, 'id': id, [name]:value}
		});
	}

	const section = <>
					<form id="addForm" onSubmit={onHandleSubmit}>
				        <div className="row">
				        	<div className="col-6">
				        		<div className="form-group m-2">
						          <label>Name</label>
						          <input type="text" className="form-control" name="name" onChange={onHandleChange} />
						        </div>
						        <div className="form-group m-2">
						          <label>Image</label>
						          <input type="text" className="form-control" name="image" onChange={onHandleChange} />
						        </div>
				        	</div>
					        <div className="col-6">
					        	<div className="form-group m-2">
						          <label>Price</label>
						          <input type="number" className="form-control" name="price" onChange={onHandleChange} />
						        </div>
						        <div className="form-group m-2">
						          <label>Desc</label>
						          <input type="text" className="form-control" name="desc" onChange={onHandleChange} />
						        </div>
					        </div>
					        <div className="form-group col-3 ml-2">
					        	<button type="submit" className="btn btn-primary">Submit</button>
					        </div>
				        </div>
				      </form>
					</>
	return (
		<div>
			<Admin_layout section={section} />
		</div>
	)
}

export default Add_product
