import React, { useState, useEffect } from 'react'
import { useParams } from 'react-router-dom'
import Layout from '../../../pages/admin_layout'
import { Link } from 'react-router-dom'


const Categories = ({ categories, data }) => {
	const { id } = useParams();
	const a = [];
	const [ amount, setAmount ] = useState([]);
	const getAmountProduct = (id) => {
		const singleProduct = data.filter(element => element.cateId === id);
		a.push(singleProduct);
		console.log(singleProduct.length);
		console.log(a);
	}
	useEffect(() => {
		document.querySelector('#test').innerHTML = a.length;
	}, [])
	const section = <>
						<table className="table">
							  <thead>
							    <tr>
							      <th scope="col">Id</th>
							      <th scope="col">Name</th>
							      <th scope="col">Amount</th>
							      <th scope="col">Delete/Edit</th>
							    </tr>
							  </thead>
							  <tbody>
							    {categories.map((category, index) => 
							    	<tr key={index}>
								      <th scope="row">{category.id}</th>
								      <td>{category.name}</td>
								      <td id="test">{getAmountProduct(category.id)}{a.length}</td>
								      <td>
								      	<button className="btn btn-danger mr-1">Delete</button>
										<Link to={'/admin/edit-product/' + "1"}>
								      		<button className="btn btn-primary">Edit</button>
										</Link>
								      </td>
								    </tr>	
							    )}
							  </tbody>
						   </table>
					</>
	return (
		<>
			<Layout section={section} />
		</>
	)
}

export default Categories
