				        <div className="row">
				        	<div className="col-6">
				        		<div className="form-group m-2">
						          <label>Name</label>
						          <input type="text" className="form-control" name="name" onChange={onHandleChange} />
						        </div>
						        <div className="form-group m-2">
						          <label>Image</label>
						          <input type="text" className="form-control" name="image" onChange={onHandleChange} />
						        </div>
				        	</div>
					        <div className="col-6">
					        	<div className="form-group m-2">
						          <label>Price</label>
						          <input type="number" className="form-control" name="price" onChange={onHandleChange} />
						        </div>
						        <div className="form-group m-2">
						          <label>Desc</label>
						          <input type="text" className="form-control" name="desc" onChange={onHandleChange} />
						        </div>
					        </div>
					        <div className="form-group col-3 ml-2">
					        	<button type="submit" className="btn btn-primary">Submit</button>
					        </div>
				        </div>
				      </form>
				      <table className="table">
						  <thead>
						    <tr>
						      <th scope="col">Id</th>
						      <th scope="col">Name</th>
						      <th scope="col">Image</th>
						      <th scope="col">Price</th>
						    </tr>
						  </thead>
						  <tbody>
						    {products.map((product, index) => 
						    	<tr>
							      <th scope="row">{product.id}</th>
							      <td>{product.name}</td>
							      <td><img src={product.image} width='50' /></td>
							      <td>{product.price}</td>
							    </tr>	
						    )}
						  </tbody>
						</table>
					</>
	return (
		<div>
			<Admin_layout section={section} />
		</div>
	)
}

export default Add_product
