import React, { useState } from "react";
import Router_website from './router';
import fakeData from './fakeData.js';
import './scss/_site-base.scss';
import './scss/_site-blocks.scss';
import './scss/_site-navbar.scss';
import './scss/style.scss';

const App = () => {
  const [ products,setProducts ] = useState(fakeData); 

  const onHandleRemove = (id) => {
    const newArr = products.filter(product => product.id != id);
    setProducts(newArr);
  }

  const onHandleAdd = (product) => {
    setProducts([...products, product]);
    console.log(products);
  }
  return (
    <Router_website data={products} onRemove={onHandleRemove} onAdd={onHandleAdd} />
  );
}

export default App
