import React, { useEffect } from 'react'
import Admin_layout from '../../../pages/admin_layout'
import { Link } from 'react-router-dom'

const CardAdmin = ({ cartAdmin }) => {
	useEffect(() => {
		for (let cart of cartAdmin) {
			console.log(cart);
			if (cart.status == 1) {
				document.querySelector('#status_cart').innerHTML = "active";
			} else {
				document.querySelector('#status_cart').innerHTML = "not active";
			}
		}

	}, [])
	const section = <>
						<table className="table">
							  <thead>
							    <tr>
							      <th scope="col">ID</th>
							      <th scope="col">Total</th>
							      <th scope="col">Detail</th>
							    </tr>
							  </thead>
							  <tbody>
							    {cartAdmin.map((single, index) => {
							    	return(
							    		<tr key={index}>
									      <th scope="row">{single.id}</th>
									      <td id="status_cart"></td>
									      <td>
									      	<Link to={'/admin/cart-detail/' + single.id}>
									      		<button type="button" className="btn btn-primary">Detail</button>
									      	</Link>
									      </td>
									    </tr>
							    	)
							    })}
							  </tbody>
						   </table>
					</>
	return (
		<>
			<Admin_layout section={section} />
		</>
	)
}

export default CardAdmin
