	}

	const onHandleAdd = (product) => {
		setProducts([...products,product]);
		console.log(product);
		console.log(products);
	}

	return (
		<>
			<ShowProducts apiData={products} onRemove={onHandleRemove} onAdd={onHandleAdd} />
		</>
	)
}

export default Products
