    console.log(products);
  }
  return (
    <Router>
      <>
        <Switch>
          <Route path="/add-product">
            <Add_product data={fakeData} reLoad={Add_product} />
          </Route>
          <Route path="/products">
            <Products />
          </Route>
          <Route path="/categories">
            <Categories />
          </Route>
          <Route path="/product/:id">
            <Product_detail reLoad={Product_detail} />
          </Route>
        </Switch>
      </>
    </Router>
  );
}

export default App
